import React from "react";

class Task extends React.Component {

    constructor(props) {
        super(props);
        this.state = {name: props.task.name, id: props.task.id, isComplete: props.task.isComplete, textDecorationLine: 'none', display: 'inline'};
    }

    delete = () => {
        this.setState({
            isComplete: 'true',
            display: 'none'
        });
    }

    complete = () => {
        this.setState({
            textDecorationLine: 'line-through'
        });
    }

    update = (name) => {
        this.setState({
            name: name
        });
    }

    render() {
        return (
            <div id={this.state.id} style={{ display:this.state.display }}>
                <h3 style={{ textDecorationLine: this.state.textDecorationLine }}>{this.state.name}</h3>
                <button onClick={this.delete}>Delete</button>
                <button onClick={this.complete}>Complete</button>
                <br></br>
                Update New Name:
                <input type="text" id={'text'+this.state.id}></input>
                <button type="submit" onSubmit={this.update(document.getElementById("text"+this.state.id).nodeValue)}>Submit</button>
            </div>
        );
    }
}


export default Task;
