import React from "react";

class Task extends React.Component {

    constructor(props) {
        super(props);
        this.state = {name: props.task.name, id: props.task.id, isComplete: props.task.isComplete, textDecorationLine: 'none', display: 'inline'};
    }

    delete = () => {
        this.setState({
            isComplete: 'true',
            display: 'none'
        });
    }

    complete = () => {
        this.setState({
            textDecorationLine: 'line-through'
        });
    }

    update = (name, id) => {
        document.getElementById('id').value = name;
    }

    render() {
        var textId = 'text' + this.state.id;
        return (
            <div style={{ display:this.state.display }}>
                <h3 id={this.state.id} style={{ textDecorationLine: this.state.textDecorationLine }}>{this.state.name}</h3>
                <button onClick={this.delete}>Delete</button>
                <button onClick={this.complete}>Complete</button>
                <br></br>
                    Update New Name:
                    <input type="text" id={textId}>Input New Name</input>
                    <button type="submit" onclick={this.update(document.getElementById(textId).value, this.state.id)}></button>
            </div>
        );
    }
}


export default Task;
