import React from "react";

class Task extends React.Component {

    constructor(props) {
        super(props);
        this.state = {name: props.task.name, id: props.task.id, isComplete: 'false', textDecorationLine: 'none'};
    }

    display() {
        if(this.state.isComplete) {
            return "none";
        }
        else return "inline";
    }
    delete() {
        this.setState({
            isComplete: true
        });
    }

    complete() {
        this.setState({
            textDecorationLine: 'line-through'
        });
    }

    render() {
        return (
            <div>
                <h3 id={this.state.id} style={{ display: {display}, textDecorationLine: this.state.textDecorationLine }}>{this.state.name}</h3> 
                <button onClick={this.delete}>Delete</button>
                <button onClick={this.complete}>Complete</button>
            </div>
        );
    }
}


export default Task;
