import React from 'react';
import Task from './task';

const Tasks = [{'name': "task1", "isComplete": "false"}, {'name': "task2", "isComplete": "true", "id": "2"}];

function createTask(name) {
  document.getElementById('createTask').value = '';
  return {'name': name, 'isComplete': 'false'}
}

function App() {
  var t = [];
  for(var i = 0; i < Tasks.length; i++) {
    t.push(<Task task={Tasks[i]}></Task>);
  }
  return(
    <div>
      <h1>My Todos</h1>
      <div>
        <p>Create New Task</p>
        <br></br>
        <p>Name: </p>
        <input type="text" id="createTask" on></input>
      </div>
      <div>{t}</div>
    </div>
  );
}

export default App;
