import React from "react";

class Task extends React.Component {

    constructor(props) {
        super(props);
        this.state = {name: props.task.name, id: props.task.id, isComplete: 'false', textDecorationLine: 'none', display: 'inline'};
    }

    delete = () => {
        this.setState({
            isComplete: 'true',
            display: 'none'
        });
    }

    complete = () => {
        this.setState({
            textDecorationLine: 'line-through'
        });
    }

    render() {
        return (
            <div id={this.state.id} display='none'>
                <h3 style={{ textDecorationLine: this.state.textDecorationLine }}>{this.state.name}</h3>
                <button onClick={this.delete}>Delete</button>
                <button onClick={this.complete}>Complete</button>
            </div>
        );
    }
}


export default Task;
