import React from 'react';
import Task from './task';

const Tasks = [{'name': "task1", "isComplete": "false"}, {'name': "task2", "isComplete": "true", "id": "2"}];

function createTask() {
  taskName = document.getElementById('createTask').value;
  document.getElementById('createTask').value = '';
  document.getElementById('listTask').value += <Task task={{'name': taskName, 'isComplete': 'false'}}></Task>
}

function App() {
  var t = [];
  for(var i = 0; i < Tasks.length; i++) {
    t.push(<Task task={Tasks[i]}></Task>);
  }
  return(
    <div>
      <h1>My Todos</h1>
      <div>
        <p>Create New Task</p>
        <br></br>
        <p>Name: </p>
        <input type="text" id="createTask"></input>
        <button type="submit" onClick={createTask}>Submit</button>
      </div>
      <div id="listTask">{t}</div>
    </div>
  );
}

export default App;
